// ü§ñ AUTONOMOUS LEAD PROCESSING
// Intelligent lead qualification and scoring

const lead = $input.first().json;
const timestamp = new Date().toISOString();

// Extract lead information
const leadData = {
  id: 'LEAD_' + Date.now() + '_' + Math.random().toString(36).substr(2, 9),
  timestamp,
  source: lead.source || 'website',
  email: lead.email,
  phone: lead.phone,
  name: lead.name,
  location: lead.location || 'Lagos',
  language: lead.language || 'english',
  legal_need: lead.legal_need || 'general',
  urgency: lead.urgency || 'medium',
  company: lead.company || null,
  raw_data: lead
};

// üß† AI-POWERED LEAD SCORING
let score = 0;
let tier = 'bronze';
let followup_priority = 'low';

// Email quality scoring
if (leadData.email && leadData.email.includes('@')) {
  score += 20;
  if (leadData.email.includes('.com') || leadData.email.includes('.ng')) score += 10;
  if (leadData.email.includes('gmail') || leadData.email.includes('yahoo')) score += 5;
}

// Phone number scoring (Nigerian format)
if (leadData.phone) {
  score += 15;
  if (leadData.phone.startsWith('+234') || leadData.phone.startsWith('0')) score += 10;
  if (leadData.phone.length >= 11) score += 5;
}

// Legal need urgency scoring
switch (leadData.urgency) {
  case 'emergency': score += 50; followup_priority = 'immediate'; break;
  case 'urgent': score += 30; followup_priority = 'high'; break;
  case 'medium': score += 15; followup_priority = 'medium'; break;
  case 'low': score += 5; followup_priority = 'low'; break;
}

// Legal need type scoring
switch (leadData.legal_need) {
  case 'emergency': score += 40; break;
  case 'document_generation': score += 25; break;
  case 'lawyer_consultation': score += 30; break;
  case 'tenancy_agreement': score += 20; break;
  case 'affidavit': score += 15; break;
  case 'power_of_attorney': score += 20; break;
  case 'general': score += 10; break;
}

// Location scoring (major Nigerian cities)
const premiumCities = ['lagos', 'abuja', 'port harcourt', 'kano', 'ibadan'];
if (premiumCities.includes(leadData.location.toLowerCase())) {
  score += 15;
}

// Company/organization scoring
if (leadData.company) {
  score += 25;
  if (leadData.company.toLowerCase().includes('law') || 
      leadData.company.toLowerCase().includes('legal') ||
      leadData.company.toLowerCase().includes('chambers')) {
    score += 20;
  }
}

// Determine tier based on score
if (score >= 80) {
  tier = 'diamond';
  followup_priority = 'immediate';
} else if (score >= 60) {
  tier = 'gold';
  followup_priority = 'high';
} else if (score >= 40) {
  tier = 'silver';
  followup_priority = 'medium';
} else {
  tier = 'bronze';
  followup_priority = 'low';
}

// Autonomous recommendation engine
let recommended_plan = 'free';
let estimated_value = 0;
let conversion_probability = 0;

if (score >= 70) {
  recommended_plan = 'premium';
  estimated_value = 12000; // ‚Ç¶1,000 x 12 months
  conversion_probability = 0.75;
} else if (score >= 50) {
  recommended_plan = 'premium';
  estimated_value = 6000; // ‚Ç¶1,000 x 6 months
  conversion_probability = 0.55;
} else if (score >= 30) {
  recommended_plan = 'free_to_premium';
  estimated_value = 3000; // ‚Ç¶1,000 x 3 months
  conversion_probability = 0.35;
} else {
  recommended_plan = 'free';
  estimated_value = 500; // Document fees
  conversion_probability = 0.15;
}

// Check for TaaS (enterprise) potential
if (leadData.company && score >= 60) {
  recommended_plan = 'taas';
  estimated_value = 50000; // ‚Ç¶50,000/month
  conversion_probability = 0.25;
}

const processedLead = {
  ...leadData,
  score,
  tier,
  followup_priority,
  recommended_plan,
  estimated_value,
  conversion_probability,
  next_action: determineNextAction(tier, leadData.urgency),
  followup_schedule: generateFollowupSchedule(tier, leadData.urgency),
  personalization: generatePersonalization(leadData),
  status: 'new',
  assigned_agent: assignAgent(tier, leadData.location),
  processed_by: 'autonomous_ai',
  processing_timestamp: timestamp
};

console.log(`üéØ Lead processed: ${leadData.name} | Score: ${score} | Tier: ${tier} | Priority: ${followup_priority}`);

function determineNextAction(tier, urgency) {
  if (urgency === 'emergency') return 'immediate_call';
  if (tier === 'diamond') return 'personal_call_within_5min';
  if (tier === 'gold') return 'whatsapp_within_15min';
  if (tier === 'silver') return 'email_within_1hour';
  return 'email_within_24hours';
}

function generateFollowupSchedule(tier, urgency) {
  const now = new Date();
  if (urgency === 'emergency') {
    return {
      immediate: new Date(now.getTime() + 2 * 60 * 1000), // 2 minutes
      followup_1: new Date(now.getTime() + 30 * 60 * 1000), // 30 minutes
      followup_2: new Date(now.getTime() + 6 * 60 * 60 * 1000) // 6 hours
    };
  }

  if (tier === 'diamond') {
    return {
      immediate: new Date(now.getTime() + 5 * 60 * 1000), // 5 minutes
      followup_1: new Date(now.getTime() + 4 * 60 * 60 * 1000), // 4 hours
      followup_2: new Date(now.getTime() + 24 * 60 * 60 * 1000), // 1 day
      followup_3: new Date(now.getTime() + 7 * 24 * 60 * 60 * 1000) // 7 days
    };
  }

  return {
    immediate: new Date(now.getTime() + 60 * 60 * 1000), // 1 hour
    followup_1: new Date(now.getTime() + 24 * 60 * 60 * 1000), // 1 day
    followup_2: new Date(now.getTime() + 3 * 24 * 60 * 60 * 1000), // 3 days
    followup_3: new Date(now.getTime() + 7 * 24 * 60 * 60 * 1000), // 7 days
    followup_4: new Date(now.getTime() + 30 * 24 * 60 * 60 * 1000) // 30 days
  };
}

function generatePersonalization(leadData) {
  return {
    greeting: getGreeting(leadData.language),
    legal_focus: leadData.legal_need,
    location_context: getLocationContext(leadData.location),
    urgency_tone: getUrgencyTone(leadData.urgency),
    language_preference: leadData.language
  };
}

function getGreeting(language) {
  const greetings = {
    english: 'Good day',
    pidgin: 'How far',
    yoruba: '·∫∏ k√∫ √†√°r·ªçÃÄ',
    hausa: 'Sannu',
    igbo: 'Ndewo'
  };
  return greetings[language] || greetings.english;
}

function getLocationContext(location) {
  const contexts = {
    lagos: 'as someone in the commercial capital of Nigeria',
    abuja: 'as someone in the federal capital territory',
    'port harcourt': 'as someone in the oil capital',
    kano: 'as someone in the northern commercial hub',
    ibadan: 'as someone in the ancient city'
  };
  return contexts[location.toLowerCase()] || 'as a valued Nigerian client';
}

function getUrgencyTone(urgency) {
  const tones = {
    emergency: 'immediate and urgent',
    urgent: 'prompt and focused',
    medium: 'professional and helpful',
    low: 'friendly and informative'
  };
  return tones[urgency] || tones.medium;
}

function assignAgent(tier, location) {
  // Autonomous agent assignment based on tier and location
  if (tier === 'diamond') return 'senior_legal_advisor';
  if (tier === 'gold') return 'legal_specialist';

  // Location-based assignment for local context
  const locationAgents = {
    lagos: 'lagos_legal_team',
    abuja: 'abuja_legal_team',
    'port harcourt': 'south_south_team',
    kano: 'northern_team',
    ibadan: 'southwest_team'
  };

  return locationAgents[location.toLowerCase()] || 'general_legal_team';
}

return [{ json: processedLead }];

