// üìß AUTONOMOUS EMAIL GENERATION
// AI-powered personalized email creation

const lead = $input.first().json;
const personalization = lead.personalization;

// Generate personalized subject line
function generateSubject(lead) {
  const subjects = {
    emergency: `üö® Immediate Legal Assistance Available for ${lead.name}`,
    diamond: `${personalization.greeting} ${lead.name}, Your Personal Legal AI Assistant Awaits`,
    gold: `Quick Legal Help for ${lead.name} - MISS Legal AI`,
    silver: `${personalization.greeting} ${lead.name}, Affordable Legal AI Solutions`,
    bronze: `Welcome ${lead.name}! Free Legal AI Consultation Available`
  };

  if (lead.urgency === 'emergency') return subjects.emergency;
  return subjects[lead.tier] || subjects.bronze;
}

