// ðŸ” AUTONOMOUS PAYMENT SECURITY VALIDATION
// Advanced security and fraud detection

const payload = $input.first().json;
const headers = $input.first().headers;

// Security validation
const signature = headers['verif-hash'];
const secretKey = 'FLWSECK-652d980d30ba7baf3feef50fdf69ae6a-1972a5bd4a3vt-X';

console.log('ðŸ”’ Payment webhook received:', {
  event: payload.event,
  status: payload.data?.status,
  tx_ref: payload.data?.tx_ref,
  amount: payload.data?.amount,
  currency: payload.data?.currency
});

// Validate webhook signature
if (signature !== secretKey) {
  console.error('ðŸš« Invalid webhook signature - Security breach attempt!');
  throw new Error('Invalid webhook signature');
}

// Extract payment data
const paymentData = payload.data;

// ðŸ¤– AUTONOMOUS FRAUD DETECTION
let fraudScore = 0;
let fraudIndicators = [];

// Check for suspicious patterns
if (paymentData.amount > 500000) { // > â‚¦500,000
  fraudScore += 20;
  fraudIndicators.push('high_amount');
}

// Check payment timing (suspicious if multiple payments in short time)
if (paymentData.customer?.email) {
  // In real implementation, check database for recent payments
  // For now, simulate check
  const recentPayments = false; // Would check database
  if (recentPayments) {
    fraudScore += 30;
    fraudIndicators.push('rapid_payments');
  }
}

// Check currency consistency
if (paymentData.currency !== 'NGN') {
  fraudScore += 15;
  fraudIndicators.push('foreign_currency');
}

// Check customer data completeness
if (!paymentData.customer?.email || !paymentData.customer?.phone) {
  fraudScore += 10;
  fraudIndicators.push('incomplete_customer_data');
}

// Determine fraud risk level
let riskLevel = 'low';
if (fraudScore >= 50) {
  riskLevel = 'high';
} else if (fraudScore >= 25) {
  riskLevel = 'medium';
}

// ðŸ’° AUTONOMOUS PAYMENT PROCESSING
const processedPayment = {
  payment_id: paymentData.id,
  tx_ref: paymentData.tx_ref,
  flw_ref: paymentData.flw_ref,
  status: paymentData.status,
  amount: parseFloat(paymentData.amount),
  currency: paymentData.currency,
  customer: paymentData.customer,
  payment_type: paymentData.payment_type,
  created_at: paymentData.created_at,

  // Fraud detection results
  fraud_score: fraudScore,
  fraud_indicators: fraudIndicators,
  risk_level: riskLevel,

  // Processing metadata
  processed_at: new Date().toISOString(),
  processed_by: 'autonomous_payment_system',
  webhook_event: payload.event,

  // Nigerian market context
  nigerian_context: {
    is_nigerian_payment: paymentData.currency === 'NGN',
    payment_method_local: ['card', 'bank_transfer', 'ussd', 'mobile_money'].includes(paymentData.payment_type),
    estimated_location: detectLocation(paymentData)
  },

  // Autonomous actions required
  requires_manual_review: riskLevel === 'high',
  auto_approve: riskLevel === 'low' && paymentData.status === 'successful',
  next_actions: determineNextActions(paymentData, riskLevel)
};

function detectLocation(paymentData) {
  // Basic location detection based on payment patterns
  if (paymentData.customer?.phone?.startsWith('+234') || paymentData.customer?.phone?.startsWith('0')) {
    return 'Nigeria';
  }
  return 'Unknown';
}

function determineNextActions(paymentData, riskLevel) {
  const actions = [];

  if (paymentData.status === 'successful' && riskLevel === 'low') {
    actions.push('activate_subscription');
    actions.push('send_welcome_email');
    actions.push('unlock_premium_features');
    actions.push('schedule_onboarding');
  } else if (paymentData.status === 'successful' && riskLevel === 'medium') {
    actions.push('verify_customer');
    actions.push('conditional_activation');
    actions.push('enhanced_monitoring');
  } else if (riskLevel === 'high') {
    actions.push('hold_activation');
    actions.push('manual_review_required');
    actions.push('security_verification');
  } else if (paymentData.status === 'failed') {
    actions.push('retry_payment');
    actions.push('contact_customer');
    actions.push('update_payment_method');
  }

  return actions;
}

console.log(`ðŸ’³ Payment processed: ${paymentData.tx_ref} | Amount: â‚¦${paymentData.amount} | Risk: ${riskLevel}`);

return [{ json: processedPayment }];

// ðŸš€ AUTONOMOUS SUBSCRIPTION ACTIVATION
// Intelligent plan determination and feature unlocking

const payment = $input.first().json;

// ðŸ§  AUTONOMOUS PLAN DETECTION
// Intelligent plan identification based on payment amount and context
let detectedPlan = 'free';
let planFeatures = [];
let subscriptionType = 'one_time';
let billingCycle = null;
let nextBillingDate = null;

const amount = payment.amount;

// Plan detection logic
if (amount >= 50000) {
  detectedPlan = 'taas';
  subscriptionType = 'subscription';
  billingCycle = 'monthly';
  planFeatures = [
    'unlimited_users_100',
    'admin_dashboard',
    'priority_support',
    'custom_branding',
    'analytics',
    'dedicated_account_manager',
    'custom_integrations',
    'emergency_escalation'
  ];
} else if (amount >= 12000) {
  detectedPlan = 'premium_annual';
  subscriptionType = 'subscription';
  billingCycle = 'annual';
  planFeatures = [
    'unlimited_voice_chat',
    'unlimited_documents',
    'priority_emergency_response',
    'lawyer_consultation_discount',
    'pdf_export',
    'whatsapp_integration',
    'multi_language_support'
  ];
} else if (amount >= 1000) {
  detectedPlan = 'premium';
  subscriptionType = 'subscription';
  billingCycle = 'monthly';
  planFeatures = [
    'unlimited_voice_chat',
    'unlimited_documents',
    'priority_support',
    'pdf_export',
    'whatsapp_integration',
    'emergency_response'
  ];
} else if (amount >= 500) {
  detectedPlan = 'daas_bundle';
  subscriptionType = 'one_time';
  planFeatures = [
    'multiple_documents',
    'voice_generation',
    'basic_support',
    'pdf_export'
  ];
} else if (amount >= 200) {
  detectedPlan = 'daas_single';
  subscriptionType = 'one_time';
  planFeatures = [
    'single_document',
    'voice_generation',
    'basic_support',
    'pdf_export'
  ];
}

// Calculate next billing date for subscriptions
if (subscriptionType === 'subscription') {
  const now = new Date();
  if (billingCycle === 'monthly') {
    nextBillingDate = new Date(now.getFullYear(), now.getMonth() + 1, now.getDate());
  } else if (billingCycle === 'annual') {
    nextBillingDate = new Date(now.getFullYear() + 1, now.getMonth(), now.getDate());
  }
}

// ðŸŽ¯ AUTONOMOUS CUSTOMER SEGMENTATION
let customerSegment = 'standard';
let priorityLevel = 'normal';
let accountManager = 'auto_assigned';

if (detectedPlan === 'taas') {
  customerSegment = 'enterprise';
  priorityLevel = 'high';
  accountManager = 'dedicated_enterprise_manager';
} else if (detectedPlan.includes('premium')) {
  customerSegment = 'premium';
  priorityLevel = 'high';
  accountManager = 'premium_success_manager';
} else if (amount >= 500) {
  customerSegment = 'valued';
  priorityLevel = 'medium';
  accountManager = 'customer_success_team';
}

// ðŸ“Š AUTONOMOUS REVENUE TRACKING
const revenueData = {
  mrr_impact: calculateMRRImpact(detectedPlan, amount, billingCycle),
  arr_impact: calculateARRImpact(detectedPlan, amount, billingCycle),
  ltv_estimate: calculateLTV(detectedPlan, amount),
  churn_risk: assessChurnRisk(detectedPlan, payment.customer),
  expansion_opportunity: assessExpansionOpportunity(detectedPlan, amount)
};

function calculateMRRImpact(plan, amount, cycle) {
  if (cycle === 'monthly') return amount;
  if (cycle === 'annual') return amount / 12;
  return 0; // One-time payments don't contribute to MRR
}

function calculateARRImpact(plan, amount, cycle) {
  if (cycle === 'monthly') return amount * 12;
  if (cycle === 'annual') return amount;
  return 0; // One-time payments don't contribute to ARR
}

function calculateLTV(plan, amount) {
  const ltv_multipliers = {
    taas: 24, // 24 months average
    premium_annual: 18,
    premium: 12,
    daas_bundle: 1,
    daas_single: 1
  };
  return amount * (ltv_multipliers[plan] || 1);
}

function assessChurnRisk(plan, customer) {
  // Basic churn risk assessment
  if (plan === 'taas') return 'low'; // Enterprise customers are sticky
  if (plan.includes('premium')) return 'medium';
  return 'high'; // One-time customers have high churn risk
}

function assessExpansionOpportunity(plan, amount) {
  if (plan === 'daas_single') return 'high'; // Can upgrade to subscription
  if (plan === 'premium' && amount === 1000) return 'medium'; // Can upgrade to annual
  if (plan === 'premium_annual') return 'low'; // Already high tier
  return 'medium';
}

// ðŸŽ‰ ACTIVATION STRATEGY
const activationPlan = {
  subscription_id: 'SUB_' + Date.now() + '_' + Math.random().toString(36).substr(2, 9),
  customer_email: payment.customer.email,
  customer_phone: payment.customer.phone,
  plan: detectedPlan,
  subscription_type: subscriptionType,
  billing_cycle: billingCycle,
  amount: amount,
  currency: payment.currency,
  features: planFeatures,
  status: 'active',
  activated_at: new Date().toISOString(),
  next_billing_date: nextBillingDate?.toISOString(),

  // Customer management
  customer_segment: customerSegment,
  priority_level: priorityLevel,
  account_manager: accountManager,

  // Revenue analytics
  revenue_data: revenueData,

  // Onboarding strategy
  onboarding_flow: determineOnboardingFlow(detectedPlan),
  welcome_sequence: generateWelcomeSequence(detectedPlan, payment.customer),

  // Success metrics
  success_metrics: {
    time_to_first_value: calculateTimeToValue(detectedPlan),
    key_activation_events: getActivationEvents(detectedPlan),
    success_milestones: getSuccessMilestones(detectedPlan)
  }
};

function determineOnboardingFlow(plan) {
  const flows = {
    taas: 'enterprise_white_glove',
    premium_annual: 'premium_guided',
    premium: 'premium_self_serve',
    daas_bundle: 'document_focused',
    daas_single: 'quick_start'
  };
  return flows[plan] || 'standard';
}

function generateWelcomeSequence(plan, customer) {
  const baseSequence = {
    immediate: 'payment_confirmation',
    day_0: 'welcome_email',
    day_1: 'feature_introduction',
    day_3: 'first_use_guidance',
    day_7: 'progress_check',
    day_30: 'satisfaction_survey'
  };

  if (plan === 'taas') {
    return {
      ...baseSequence,
      immediate: 'enterprise_welcome_call_scheduled',
      day_0: 'account_manager_introduction',
      day_1: 'custom_onboarding_session'
    };
  }

  return baseSequence;
}

function calculateTimeToValue(plan) {
  const timeToValue = {
    taas: '24_hours', // Enterprise needs setup time
    premium_annual: '1_hour',
    premium: '30_minutes',
    daas_bundle: '15_minutes',
    daas_single: '5_minutes'
  };
  return timeToValue[plan] || '30_minutes';
}

function getActivationEvents(plan) {
  const events = {
    taas: ['admin_dashboard_setup', 'team_member_invitation', 'first_bulk_document'],
    premium: ['voice_chat_completion', 'document_generation', 'emergency_contact_setup'],
    daas: ['document_generation_complete', 'pdf_download']
  };
  return events[plan] || events.premium;
}

function getSuccessMilestones(plan) {
  const milestones = {
    taas: ['10_users_active', '50_documents_generated', 'admin_training_complete'],
    premium: ['5_voice_chats', '3_documents_generated', 'emergency_setup'],
    daas: ['document_completed', 'satisfaction_rating']
  };
  return milestones[plan] || milestones.premium;
}

console.log(`ðŸš€ Subscription activated: ${detectedPlan} for ${payment.customer.email} - â‚¦${amount}`);

return [{ json: activationPlan }];

// ðŸ”„ AUTONOMOUS FAILED PAYMENT RECOVERY
// Intelligent retry strategy and customer communication

const payment = $input.first().json;

// ðŸ“Š FAILURE ANALYSIS
const failureReasons = {
  'insufficient_funds': {
    retry_strategy: 'delayed_retry',
    retry_delay: '24_hours',
    customer_message: 'insufficient_funds_guidance',
    retry_count_max: 3
  },
  'expired_card': {
    retry_strategy: 'update_payment_method',
    retry_delay: 'immediate',
    customer_message: 'card_update_required',
    retry_count_max: 1
  },
  'network_error': {
    retry_strategy: 'immediate_retry',
    retry_delay: '5_minutes',
    customer_message: 'technical_issue',
    retry_count_max: 5
  },
  'invalid_cvv': {
    retry_strategy: 'update_payment_method',
    retry_delay: 'immediate',
    customer_message: 'payment_details_incorrect',
    retry_count_max: 2
  },
  'bank_decline': {
    retry_strategy: 'alternative_payment_method',
    retry_delay: '1_hour',
    customer_message: 'bank_declined_guidance',
    retry_count_max: 2
  }
};

// Detect failure reason (simplified - in real implementation, would analyze Flutterwave response)
let detectedReason = 'network_error'; // Default
if (payment.processor_response) {
  const response = payment.processor_response.toLowerCase();
  if (response.includes('insufficient') || response.includes('balance')) {
    detectedReason = 'insufficient_funds';
  } else if (response.includes('expired') || response.includes('expiry')) {
    detectedReason = 'expired_card';
  } else if (response.includes('cvv') || response.includes('security')) {
    detectedReason = 'invalid_cvv';
  } else if (response.includes('declined') || response.includes('reject')) {
    detectedReason = 'bank_decline';
  }
}

const failureStrategy = failureReasons[detectedReason];

// ðŸ¤– AUTONOMOUS RECOVERY PLAN
const recoveryPlan = {
  payment_id: payment.payment_id,
  tx_ref: payment.tx_ref,
  customer_email: payment.customer.email,
  customer_phone: payment.customer.phone,
  failure_reason: detectedReason,
  failure_timestamp: new Date().toISOString(),

  // Recovery strategy
  retry_strategy: failureStrategy.retry_strategy,
  retry_delay: failureStrategy.retry_delay,
  max_retries: failureStrategy.retry_count_max,
  current_retry_count: 0,

  // Customer communication
  customer_message_type: failureStrategy.customer_message,
  communication_channels: ['email', 'whatsapp', 'sms'],
  urgency_level: determineUrgencyLevel(payment.amount),

  // Alternative options
  alternative_payment_methods: [
    'bank_transfer',
    'ussd_payment',
    'mobile_money',
    'different_card'
  ],

  // Incentives to retry
  retry_incentives: generateRetryIncentives(payment.amount, detectedReason),

  // Next actions
  scheduled_actions: [
    {
      action: 'send_failure_notification',
      schedule: 'immediate',
      channels: ['email', 'whatsapp']
    },
    {
      action: 'provide_alternative_methods',
      schedule: '1_hour',
      channels: ['email']
    },
    {
      action: 'retry_payment',
      schedule: failureStrategy.retry_delay,
      condition: 'customer_authorizes'
    },
    {
      action: 'offer_assistance',
      schedule: '24_hours',
      channels: ['phone_call']
    }
  ]
};

function determineUrgencyLevel(amount) {
  if (amount >= 50000) return 'high'; // Enterprise customer
  if (amount >= 1000) return 'medium'; // Premium customer
  return 'low';
}

function generateRetryIncentives(amount, reason) {
  const incentives = [];

  if (reason === 'insufficient_funds') {
    incentives.push('payment_plan_option');
    incentives.push('next_month_discount_10_percent');
  } else if (reason === 'expired_card' || reason === 'invalid_cvv') {
    incentives.push('priority_support');
    incentives.push('extended_trial_period');
  } else {
    incentives.push('technical_support');
    incentives.push('alternative_payment_bonus');
  }

  // High-value customer incentives
  if (amount >= 50000) {
    incentives.push('dedicated_account_manager');
    incentives.push('custom_payment_terms');
  } else if (amount >= 1000) {
    incentives.push('priority_customer_service');
    incentives.push('extended_features_trial');
  }

  return incentives;
}

// ðŸ“§ GENERATE RECOVERY COMMUNICATION
const communicationPlan = {
  immediate_email: {
    subject: generateFailureEmailSubject(detectedReason, payment.amount),
    content: generateFailureEmailContent(detectedReason, payment, recoveryPlan),
    priority: 'high',
    include_retry_link: true
  },
  whatsapp_message: {
    message: generateWhatsAppMessage(detectedReason, payment.customer.name),
    include_payment_link: true,
    timing: 'immediate'
  },
  followup_sequence: generateFollowupSequence(detectedReason, payment.amount)
};

function generateFailureEmailSubject(reason, amount) {
  const subjects = {
    insufficient_funds: 'Payment Issue - Easy Solutions Available',
    expired_card: 'Update Your Payment Method - Quick Fix Needed',
    network_error: 'Technical Issue with Your Payment - We\'re Here to Help',
    invalid_cvv: 'Payment Details Update Required',
    bank_decline: 'Alternative Payment Options Available'
  };

  if (amount >= 50000) {
    return `[Priority] ${subjects[reason]} - Enterprise Support Available`;
  }

  return subjects[reason] || 'Payment Issue - We\'re Here to Help';
}

function generateFailureEmailContent(reason, payment, recovery) {
  let content = `Dear ${payment.customer.name || 'Valued Customer'},\n\n`;

  // Reason-specific opening
  const openings = {
    insufficient_funds: 'Your payment couldn\'t be processed due to insufficient funds. This happens sometimes, and we have easy solutions.',
    expired_card: 'Your payment card appears to have expired. This is a quick fix!',
    network_error: 'We experienced a technical issue while processing your payment. Don\'t worry - this is usually temporary.',
    invalid_cvv: 'There was an issue with your card security code. This is easily resolved.',
    bank_decline: 'Your bank declined the payment. We have alternative options available.'
  };

  content += openings[reason] || 'We encountered an issue with your payment, but we\'re here to help resolve it quickly.';
  content += '\n\n';

  // Solutions
  content += 'ðŸ”§ Easy Solutions:\n';
  recovery.retry_incentives.forEach(incentive => {
    const incentiveTexts = {
      payment_plan_option: 'â€¢ Set up a payment plan that works for your budget',
      next_month_discount_10_percent: 'â€¢ Get 10% off your next month when you retry',
      priority_support: 'â€¢ Get priority support to resolve this quickly',
      extended_trial_period: 'â€¢ Enjoy extended trial period while you update payment',
      technical_support: 'â€¢ Get free technical support for payment issues',
      alternative_payment_bonus: 'â€¢ Try alternative payment method with bonus features',
      dedicated_account_manager: 'â€¢ Get assigned dedicated account manager',
      custom_payment_terms: 'â€¢ Discuss custom payment terms that work for you'
    };
    content += incentiveTexts[incentive] || '';
    content += '\n';
  });

  content += '\n';
  content += 'ðŸš€ Retry Your Payment: [RETRY_PAYMENT_LINK]\n';
  content += 'ðŸ’¬ Need Help? WhatsApp us: +234-XXX-XXXX\n';
  content += 'ðŸ“§ Email Support: payments@miss-legal.ai\n\n';

  content += 'We\'re committed to making this right for you.\n\n';
  content += 'Best regards,\n';
  content += 'MISS Legal AI Payment Team\n';
  content += 'Powered by ODIA AI Solutions';

  return content;
}

function generateWhatsAppMessage(reason, customerName) {
  const messages = {
    insufficient_funds: `Hi ${customerName}, your MISS Legal AI payment needs a quick retry. We have easy payment plans available! ðŸ’³`,
    expired_card: `Hi ${customerName}, just need to update your card details for MISS Legal AI. Quick 2-minute fix! ðŸ”„`,
    network_error: `Hi ${customerName}, technical hiccup with your MISS Legal AI payment. Retrying now should work! âš¡`,
    invalid_cvv: `Hi ${customerName}, need to verify your card security code for MISS Legal AI. Easy fix! ðŸ”`,
    bank_decline: `Hi ${customerName}, your bank declined the MISS Legal AI payment. We have other options! ðŸ¦`
  };

  return messages[reason] || `Hi ${customerName}, quick payment issue with MISS Legal AI. We're here to help! ðŸ’ª`;
}

function generateFollowupSequence(reason, amount) {
  const baseSequence = [
    { delay: '1_hour', action: 'send_alternative_methods' },
    { delay: '24_hours', action: 'personal_outreach' },
    { delay: '3_days', action: 'final_reminder' }
  ];

  if (amount >= 50000) {
    return [
      { delay: '30_minutes', action: 'account_manager_call' },
      { delay: '2_hours', action: 'custom_payment_solution' },
      { delay: '1_day', action: 'executive_review' }
    ];
  }

  return baseSequence;
}

const fullRecoveryPlan = {
  ...recoveryPlan,
  communication_plan: communicationPlan
};

console.log(`ðŸ”„ Payment recovery initiated: ${payment.tx_ref} - Reason: ${detectedReason}`);

return [{ json: fullRecoveryPlan }];

// ðŸ“Š AUTONOMOUS REVENUE ANALYTICS & INSIGHTS
// Real-time revenue tracking and business intelligence

const subscription = $input.first().json;

// ðŸ“ˆ REAL-TIME REVENUE METRICS
const now = new Date();
const currentMonth = now.getMonth();
const currentYear = now.getFullYear();

// Calculate revenue impact
const revenueMetrics = {
  // Monthly Recurring Revenue
  mrr_addition: subscription.revenue_data.mrr_impact,
  mrr_date: now.toISOString(),

  // Annual Recurring Revenue
  arr_addition: subscription.revenue_data.arr_impact,

  // Customer metrics
  new_customer: true,
  customer_segment: subscription.customer_segment,
  plan_tier: subscription.plan,

  // Business intelligence
  revenue_quality_score: calculateRevenueQualityScore(subscription),
  expansion_potential: subscription.revenue_data.expansion_opportunity,
  churn_risk_level: subscription.revenue_data.churn_risk,

  // Geographic analytics
  market_segment: 'nigeria',
  customer_location: detectCustomerLocation(subscription),

  // Product analytics
  acquisition_channel: detectAcquisitionChannel(subscription),
  conversion_time: 'immediate', // Since this is post-payment
  plan_preference: subscription.plan,

  // Forecasting data
  ltv_contribution: subscription.revenue_data.ltv_estimate,
  payback_period: calculatePaybackPeriod(subscription),
  growth_trajectory: assessGrowthTrajectory(subscription)
};

function calculateRevenueQualityScore(subscription) {
  let score = 0;

  // Plan quality scoring
  const planScores = {
    taas: 100,
    premium_annual: 90,
    premium: 75,
    daas_bundle: 50,
    daas_single: 25
  };
  score += planScores[subscription.plan] || 0;

  // Payment method reliability
  if (subscription.billing_cycle === 'annual') score += 20;
  if (subscription.amount >= 50000) score += 15; // High-value customer

  // Customer segment bonus
  if (subscription.customer_segment === 'enterprise') score += 10;

  return Math.min(score, 100);
}

function detectCustomerLocation(subscription) {
  // In real implementation, would use payment data, phone numbers, etc.
  const phone = subscription.customer_phone;
  if (phone?.startsWith('+234') || phone?.startsWith('0')) {
    return 'nigeria';
  }
  return 'unknown';
}

function detectAcquisitionChannel(subscription) {
  // In real implementation, would track UTM parameters, referrers, etc.
  // For now, simulate based on plan type
  if (subscription.plan === 'taas') return 'enterprise_sales';
  if (subscription.plan.includes('premium')) return 'organic_website';
  return 'website_direct';
}

function calculatePaybackPeriod(subscription) {
  // Simplified payback calculation
  const cac = 5000; // Estimated Customer Acquisition Cost in NGN
  const monthlyRevenue = subscription.revenue_data.mrr_impact;

  if (monthlyRevenue > 0) {
    return Math.ceil(cac / monthlyRevenue);
  }
  return null;
}

function assessGrowthTrajectory(subscription) {
  if (subscription.plan === 'taas') return 'exponential';
  if (subscription.plan.includes('premium')) return 'steady';
  if (subscription.plan.includes('daas')) return 'conversion_opportunity';
  return 'stable';
}

// ðŸŽ¯ AUTONOMOUS BUSINESS INSIGHTS
const businessInsights = {
  // Revenue health
  revenue_health: {
    quality_score: revenueMetrics.revenue_quality_score,
    sustainability: subscription.billing_cycle === 'annual' ? 'high' : 'medium',
    growth_potential: revenueMetrics.expansion_potential
  },

  // Customer success predictions
  success_predictions: {
    onboarding_success_probability: calculateOnboardingSuccessProbability(subscription),
    feature_adoption_likelihood: predictFeatureAdoption(subscription),
    retention_probability: calculateRetentionProbability(subscription)
  },

  // Business actions
  recommended_actions: generateBusinessActions(subscription, revenueMetrics),

  // Market insights
