function calculateOnboardingSuccessProbability(subscription) {
  let probability = 0.5; // Base 50%

  if (subscription.plan === 'taas') probability += 0.3; // Enterprise has high success
  if (subscription.plan.includes('premium')) probability += 0.2;
  if (subscription.billing_cycle === 'annual') probability += 0.15;
  if (subscription.amount >= 12000) probability += 0.1;

  return Math.min(probability, 0.95);
}

function predictFeatureAdoption(subscription) {
  const adoptionLikelihood = {
    voice_chat: subscription.plan !== 'daas_single' ? 0.9 : 0.3,
    document_generation: 0.95, // Core feature
    emergency_response: subscription.plan.includes('premium') || subscription.plan === 'taas' ? 0.8 : 0.4,
    lawyer_consultation: subscription.plan === 'taas' ? 0.9 : 0.6,
    whatsapp_integration: 0.85 // Popular in Nigeria
  };

  return adoptionLikelihood;
}

function calculateRetentionProbability(subscription) {
  const retentionRates = {
    taas: 0.95, // Enterprise customers are sticky
    premium_annual: 0.85,
    premium: 0.75,
    daas_bundle: 0.4,
    daas_single: 0.2
  };

  return retentionRates[subscription.plan] || 0.5;
}

function generateBusinessActions(subscription, metrics) {
  const actions = [];

  // High-value customer actions
  if (subscription.plan === 'taas') {
    actions.push('assign_dedicated_success_manager');
    actions.push('schedule_quarterly_business_review');
    actions.push('monitor_usage_metrics_weekly');
  }

  // Premium customer actions
  if (subscription.plan.includes('premium')) {
    actions.push('enroll_in_premium_onboarding');
    actions.push('provide_advanced_feature_training');
    actions.push('monitor_feature_adoption');
  }

  // Conversion opportunity actions
  if (subscription.plan.includes('daas')) {
    actions.push('track_usage_for_subscription_upsell');
    actions.push('send_subscription_conversion_campaigns');
    actions.push('provide_subscription_value_demonstration');
  }

  // Churn risk mitigation
  if (metrics.churn_risk_level === 'high') {
    actions.push('implement_early_warning_monitoring');
    actions.push('proactive_customer_success_outreach');
  }

  return actions;
}

function assessPriceOptimization(subscription) {
  if (subscription.plan === 'daas_single' && subscription.amount === 200) {
    return 'test_bundle_pricing';
  }
  if (subscription.plan === 'premium' && subscription.amount === 1000) {
    return 'test_annual_discount';
  }
  return 'current_pricing_optimal';
}

