// ü§ñ AUTONOMOUS CUSTOMER SUCCESS INTELLIGENCE
// Personalized onboarding and success pathway generation

const customer = $input.first().json;

// üß† CUSTOMER INTELLIGENCE ANALYSIS
const customerProfile = {
  customer_id: customer.customer_id || 'CUST_' + Date.now(),
  email: customer.email,
  phone: customer.phone,
  name: customer.name,
  plan: customer.plan,
  subscription_type: customer.subscription_type,
  onboarding_started: new Date().toISOString(),

  // Nigerian market context
  location: customer.location || detectLocationFromPhone(customer.phone),
  language_preference: customer.language || 'english',
  legal_expertise_level: assessLegalExpertise(customer),
  technology_comfort_level: assessTechComfort(customer),

  // Business context
  use_case_primary: customer.legal_need || 'general',
  organization_type: customer.company ? 'business' : 'individual',
  urgency_profile: customer.urgency || 'medium',

  // Success prediction
  success_likelihood: calculateSuccessLikelihood(customer),
  risk_factors: identifyRiskFactors(customer),
  opportunity_factors: identifyOpportunityFactors(customer)
};

function detectLocationFromPhone(phone) {
  if (!phone) return 'lagos'; // Default

  const nigerianCodes = {
    '0701': 'airtel_nationwide',
    '0708': 'airtel_nationwide', 
    '0802': 'airtel_nationwide',
    '0808': 'airtel_nationwide',
    '0812': 'airtel_nationwide',
    '0701': 'mtn_nationwide',
    '0703': 'mtn_nationwide',
    '0706': 'mtn_nationwide',
    '0803': 'mtn_nationwide',
    '0806': 'mtn_nationwide',
    '0810': 'mtn_nationwide',
    '0813': 'mtn_nationwide',
    '0814': 'mtn_nationwide',
    '0815': 'mtn_nationwide',
    '0816': 'mtn_nationwide',
    '0903': 'mtn_nationwide',
    '0906': 'mtn_nationwide'
  };

  // Simple location detection - in real implementation would be more sophisticated
  return 'nigeria_mobile'; // Placeholder
}

function assessLegalExpertise(customer) {
  if (customer.company && customer.company.toLowerCase().includes('law')) return 'expert';
  if (customer.legal_need && ['tenancy_agreement', 'power_of_attorney'].includes(customer.legal_need)) return 'intermediate';
  return 'beginner';
}

function assessTechComfort(customer) {
  // Basic assessment - would be enhanced with actual usage data
  if (customer.plan === 'taas') return 'high'; // Enterprise users typically tech-savvy
  if (customer.age && customer.age < 35) return 'high';
  if (customer.age && customer.age > 60) return 'low';
  return 'medium';
}

function calculateSuccessLikelihood(customer) {
  let score = 0.5; // Base 50%

  // Plan-based scoring
  const planScores = {
    taas: 0.9,
    premium_annual: 0.8,
    premium: 0.7,
    daas_bundle: 0.5,
    daas_single: 0.3
  };
  score = planScores[customer.plan] || 0.5;

  // Adjustment factors
  if (customer.legal_need === 'emergency') score += 0.1; // Urgent need drives success
  if (customer.company) score += 0.1; // Business users are more committed
  if (customer.language !== 'english') score += 0.05; // Local language users are sticky

  return Math.min(score, 0.95);
}

function identifyRiskFactors(customer) {
  const risks = [];

  if (customer.plan === 'daas_single') risks.push('one_time_purchase_churn_risk');
  if (!customer.phone) risks.push('incomplete_contact_info');
  if (customer.urgency === 'low') risks.push('low_engagement_risk');
  if (!customer.company && customer.plan === 'taas') risks.push('plan_mismatch_risk');

  return risks;
}

function identifyOpportunityFactors(customer) {
  const opportunities = [];

  if (customer.plan === 'daas_single') opportunities.push('subscription_conversion_opportunity');
  if (customer.company) opportunities.push('team_expansion_opportunity');
  if (customer.legal_need === 'emergency') opportunities.push('premium_service_upsell');
  if (customer.location === 'lagos' || customer.location === 'abuja') opportunities.push('lawyer_network_upsell');

  return opportunities;
}

// üéØ AUTONOMOUS ONBOARDING PATH GENERATION
const onboardingPath = generateOnboardingPath(customerProfile);

function generateOnboardingPath(profile) {
  const basePath = {
    total_duration: '7_days',
    success_milestones: [],
    personalized_content: [],
    automation_sequences: [],
    human_touchpoints: []
  };

  // Plan-specific onboarding
  if (profile.plan === 'taas') {
    return {
      ...basePath,
      total_duration: '14_days',
      success_milestones: [
        { day: 1, milestone: 'admin_dashboard_access', weight: 0.2 },
        { day: 3, milestone: 'team_member_setup', weight: 0.3 },
        { day: 7, milestone: 'first_bulk_documents', weight: 0.3 },
        { day: 14, milestone: 'full_feature_adoption', weight: 0.2 }
      ],
      personalized_content: [
        { day: 0, content: 'enterprise_welcome_video', language: profile.language_preference },
        { day: 1, content: 'admin_setup_guide', format: 'interactive_tutorial' },
        { day: 3, content: 'team_onboarding_best_practices', format: 'pdf_guide' },
        { day: 7, content: 'advanced_features_workshop', format: 'live_session' }
      ],
      automation_sequences: [
        { trigger: 'day_0', action: 'assign_dedicated_success_manager' },
        { trigger: 'day_1', action: 'schedule_onboarding_call' },
        { trigger: 'milestone_missed', action: 'intervention_outreach' },
        { trigger: 'day_30', action: 'quarterly_business_review_setup' }
      ],
      human_touchpoints: [
        { day: 1, type: 'onboarding_call', duration: '45_minutes' },
        { day: 7, type: 'progress_check', duration: '30_minutes' },
        { day: 14, type: 'success_review', duration: '30_minutes' }
      ]
    };
  } else if (profile.plan.includes('premium')) {
    return {
      ...basePath,
      total_duration: '7_days',
      success_milestones: [
        { day: 1, milestone: 'first_voice_chat', weight: 0.3 },
        { day: 2, milestone: 'document_generation', weight: 0.4 },
        { day: 4, milestone: 'emergency_contact_setup', weight: 0.2 },
        { day: 7, milestone: 'feature_exploration', weight: 0.1 }
      ],
      personalized_content: [
        { day: 0, content: 'premium_welcome_sequence', language: profile.language_preference },
        { day: 1, content: 'voice_chat_tutorial', format: 'interactive_demo' },
        { day: 2, content: 'document_templates_guide', format: 'video_series' },
        { day: 4, content: 'emergency_features_overview', format: 'infographic' }
      ],
      automation_sequences: [
        { trigger: 'day_0', action: 'send_welcome_kit' },
        { trigger: 'first_login', action: 'start_guided_tour' },
        { trigger: 'milestone_achieved', action: 'celebration_message' },
        { trigger: 'inactivity_48h', action: 'gentle_nudge_sequence' }
      ],
      human_touchpoints: [
        { day: 3, type: 'check_in_email', personalized: true },
        { day: 7, type: 'success_survey', with_offer: true }
      ]
    };
  } else {
    // DaaS or free plan onboarding
    return {
      ...basePath,
      total_duration: '3_days',
      success_milestones: [
        { day: 0, milestone: 'first_document_request', weight: 0.6 },
        { day: 1, milestone: 'document_completion', weight: 0.3 },
        { day: 3, milestone: 'satisfaction_rating', weight: 0.1 }
      ],
      personalized_content: [
        { day: 0, content: 'quick_start_guide', language: profile.language_preference },
        { day: 1, content: 'document_tips', format: 'quick_video' },
        { day: 2, content: 'upgrade_benefits', format: 'comparison_chart' }
      ],
      automation_sequences: [
        { trigger: 'day_0', action: 'send_quick_start_email' },
        { trigger: 'document_completed', action: 'upsell_sequence_start' },
        { trigger: 'day_3', action: 'feedback_request' }
      ],
      human_touchpoints: [
        { day: 2, type: 'upgrade_offer_email', personalized: true }
      ]
    };
  }
}

// üîÑ AUTONOMOUS SUCCESS MONITORING SYSTEM
const successMonitoring = {
  monitoring_frequency: profile.plan === 'taas' ? 'daily' : 'weekly',
  key_metrics: generateKeyMetrics(profile),
  early_warning_signals: generateWarningSignals(profile),
  intervention_triggers: generateInterventionTriggers(profile),
  success_rewards: generateSuccessRewards(profile)
};

function generateKeyMetrics(profile) {
  const metrics = {
    engagement_score: {
      calculation: 'login_frequency + feature_usage + content_consumption',
      target: profile.plan === 'taas' ? 80 : 60,
      weight: 0.3
    },
    feature_adoption: {
      calculation: 'features_used / features_available',
      target: profile.plan === 'taas' ? 0.7 : 0.4,
      weight: 0.4
    },
    value_realization: {
      calculation: 'milestones_achieved / milestones_planned',
      target: 0.8,
      weight: 0.3
    }
  };

  // Plan-specific metrics
  if (profile.plan === 'taas') {
    metrics.team_activation = {
      calculation: 'active_users / invited_users',
      target: 0.6,
      weight: 0.2
    };
  }

  return metrics;
}

function generateWarningSignals(profile) {
  const signals = [
    { signal: 'no_login_48h', severity: 'medium', action: 'engagement_outreach' },
    { signal: 'no_feature_usage_7d', severity: 'high', action: 'intervention_call' },
    { signal: 'support_tickets_increase', severity: 'medium', action: 'proactive_support' },
    { signal: 'negative_feedback', severity: 'high', action: 'immediate_follow_up' }
  ];

  if (profile.plan === 'taas') {
    signals.push(
      { signal: 'admin_inactive_3d', severity: 'high', action: 'admin_check_in' },
      { signal: 'low_team_adoption', severity: 'medium', action: 'training_offer' }
    );
  }

  return signals;
}

function generateInterventionTriggers(profile) {
  return {
    low_engagement: {
      threshold: 'engagement_score < 30',
      intervention: 'personalized_success_coaching',
      urgency: 'high'
    },
    feature_adoption_stall: {
      threshold: 'feature_adoption < 0.2 AND days_since_signup > 7',
      intervention: 'guided_feature_tour',
      urgency: 'medium'
    },
    value_realization_delay: {
      threshold: 'value_realization < 0.3 AND days_since_signup > 14',
      intervention: 'success_manager_assignment',
      urgency: 'high'
    },
    churn_risk_detected: {
      threshold: 'multiple_warning_signals AND engagement_declining',
      intervention: 'retention_campaign',
      urgency: 'critical'
    }
  };
}

function generateSuccessRewards(profile) {
  const rewards = {
    milestone_achievements: [
      { milestone: 'first_week_complete', reward: 'feature_unlock_bonus' },
      { milestone: 'power_user_status', reward: 'premium_template_access' },
      { milestone: 'referral_made', reward: 'account_credit' }
    ],
    loyalty_program: {
      points_per_action: {
        document_generated: 10,
        voice_chat_completed: 5,
        feature_explored: 3,
        feedback_provided: 15
      },
      redemption_options: [
        { points: 100, reward: 'free_lawyer_consultation' },
        { points: 200, reward: 'premium_feature_unlock' },
        { points: 500, reward: 'account_upgrade_discount' }
      ]
    }
  };

  if (profile.plan === 'taas') {
    rewards.enterprise_rewards = [
      { achievement: 'full_team_onboarded', reward: 'additional_user_licenses' },
      { achievement: 'high_usage_month', reward: 'dedicated_training_session' }
    ];
  }

  return rewards;
}

// üöÄ COMPREHENSIVE CUSTOMER SUCCESS PACKAGE
const customerSuccessPackage = {
  customer_profile: customerProfile,
  onboarding_path: onboardingPath,
  success_monitoring: successMonitoring,

  // Immediate actions
  immediate_actions: [
    {
      action: 'send_personalized_welcome',
      timing: 'immediate',
      personalization: {
        language: customerProfile.language_preference,
        plan_focus: customerProfile.plan,
        use_case: customerProfile.use_case_primary
      }
    },
    {
      action: 'setup_success_tracking',
      timing: 'immediate',
      metrics: successMonitoring.key_metrics
    },
    {
      action: 'schedule_onboarding_sequence',
      timing: '5_minutes',
      sequence: onboardingPath.automation_sequences
    }
  ],

  // Success prediction
  success_prediction: {
    likelihood: customerProfile.success_likelihood,
    key_success_factors: customerProfile.opportunity_factors,
    risk_mitigation_plan: generateRiskMitigationPlan(customerProfile.risk_factors),
    expected_milestones: onboardingPath.success_milestones
  },

  // Long-term strategy
  long_term_strategy: {
    retention_tactics: generateRetentionTactics(customerProfile),
    expansion_opportunities: generateExpansionOpportunities(customerProfile),
    advocacy_potential: assessAdvocacyPotential(customerProfile)
  }
};

function generateRiskMitigationPlan(risks) {
  const mitigationPlan = {};

  risks.forEach(risk => {
    switch(risk) {
      case 'one_time_purchase_churn_risk':
        mitigationPlan[risk] = {
          strategy: 'subscription_conversion_campaign',
          timeline: '30_days',
          tactics: ['value_demonstration', 'limited_time_offer', 'usage_analytics']
        };
        break;
      case 'low_engagement_risk':
        mitigationPlan[risk] = {
          strategy: 'engagement_boost_program',
          timeline: '14_days',
          tactics: ['gamification', 'personal_coaching', 'peer_community']
        };
        break;
      case 'plan_mismatch_risk':
        mitigationPlan[risk] = {
          strategy: 'plan_optimization_consultation',
          timeline: '7_days',
          tactics: ['usage_analysis', 'right_sizing_recommendation', 'migration_assistance']
        };
        break;
    }
  });

  return mitigationPlan;
}

function generateRetentionTactics(profile) {
  const tactics = ['regular_check_ins', 'value_reinforcement', 'feature_education'];

  if (profile.plan === 'taas') {
    tactics.push('quarterly_business_reviews', 'executive_relationship_building');
  }

  if (profile.technology_comfort_level === 'low') {
    tactics.push('simplified_interfaces', 'video_tutorials', 'phone_support_priority');
  }

  return tactics;
}

function generateExpansionOpportunities(profile) {
  const opportunities = [];

  if (profile.plan === 'daas_single') {
    opportunities.push({
      type: 'subscription_upgrade',
      target_plan: 'premium',
      value_proposition: 'unlimited_access_cost_savings',
      timeline: '60_days'
    });
  }

  if (profile.organization_type === 'business' && profile.plan !== 'taas') {
    opportunities.push({
      type: 'team_expansion',
      target_plan: 'taas',
      value_proposition: 'team_collaboration_efficiency',
      timeline: '90_days'
    });
  }

  return opportunities;
}

function assessAdvocacyPotential(profile) {
  let potential = 'medium';

  if (profile.plan === 'taas' && profile.success_likelihood > 0.8) {
    potential = 'high';
  } else if (profile.legal_expertise_level === 'expert') {
    potential = 'high';
  } else if (profile.success_likelihood < 0.4) {
    potential = 'low';
  }

  return {
    level: potential,
    advocacy_programs: potential === 'high' ? ['case_study', 'referral_program', 'speaking_opportunities'] : ['referral_program'],
    timeline: potential === 'high' ? '90_days' : '180_days'
  };
}

console.log(`üöÄ Customer success package created for ${customerProfile.name} - ${customerProfile.plan} plan`);

return [{ json: customerSuccessPackage }];

