  if (customer.plan.includes('premium')) {
    return {
      health_score: 30,
      support_tickets: 2,
      negative_feedback: 1,
      inactivity_days: 5
    };
  }
  return {
    health_score: 20,
    support_tickets: 3,
    negative_feedback: 2,
    inactivity_days: 7
