function generateNextGoal(currentMilestone, allMilestones) {
  const currentIndex = allMilestones.findIndex(m => m.milestone === currentMilestone);
  const nextMilestone = allMilestones[currentIndex + 1];

  return nextMilestone ? {
    milestone: nextMilestone.milestone,
    description: `next_goal_${nextMilestone.milestone}`,
    estimated_completion: `${nextMilestone.day - allMilestones[currentIndex].day}_days`,
    reward_preview: generateMilestoneReward(nextMilestone.milestone, 'preview')
  } : {
    milestone: 'graduation_to_power_user',
    description: 'become_miss_legal_ai_expert',
    reward_preview: { type: 'status_upgrade', value: 'power_user_benefits' }
  };
}

