function calculateBusinessHealth(analytics) {
  let healthScore = 0;
  let maxScore = 0;

  // Revenue health (30%)
  const revenueGrowth = parseFloat(analytics.revenue_analytics.revenue_trends.monthly_growth);
  if (revenueGrowth > 0.2) healthScore += 30;
  else if (revenueGrowth > 0.1) healthScore += 25;
  else if (revenueGrowth > 0.05) healthScore += 20;
  else healthScore += 10;
  maxScore += 30;

  // Customer satisfaction (25%)
  const nps = analytics.business_metrics.monthly_performance.net_promoter_score;
  if (nps > 70) healthScore += 25;
  else if (nps > 50) healthScore += 20;
  else if (nps > 30) healthScore += 15;
  else healthScore += 10;
  maxScore += 25;

  // System performance (20%)
  const uptime = parseFloat(analytics.operational_metrics.system_performance.server_uptime);
  if (uptime > 0.99) healthScore += 20;
  else if (uptime > 0.98) healthScore += 18;
  else if (uptime > 0.95) healthScore += 15;
  else healthScore += 10;
  maxScore += 20;

  // Market position (15%)
  const userGrowth = analytics.business_metrics.daily_metrics.new_signups;
  if (userGrowth > 50) healthScore += 15;
  else if (userGrowth > 30) healthScore += 12;
  else if (userGrowth > 20) healthScore += 10;
  else healthScore += 5;
  maxScore += 15;

  // Financial efficiency (10%)
  const grossMargin = parseFloat(analytics.revenue_analytics.profitability.gross_margin);
  if (grossMargin > 0.8) healthScore += 10;
  else if (grossMargin > 0.7) healthScore += 8;
  else if (grossMargin > 0.6) healthScore += 6;
  else healthScore += 3;
  maxScore += 10;

  const finalScore = Math.round((healthScore / maxScore) * 100);

  return {
    overall_score: finalScore,
    rating: finalScore > 85 ? 'excellent' : finalScore > 70 ? 'good' : finalScore > 55 ? 'fair' : 'needs_improvement',
    components: {
      revenue_health: Math.round((revenueGrowth > 0.2 ? 30 : revenueGrowth > 0.1 ? 25 : 20) / 30 * 100),
      customer_satisfaction: Math.round((nps > 70 ? 25 : nps > 50 ? 20 : 15) / 25 * 100),
      system_performance: Math.round((uptime > 0.99 ? 20 : uptime > 0.98 ? 18 : 15) / 20 * 100),
      market_position: Math.round((userGrowth > 50 ? 15 : userGrowth > 30 ? 12 : 10) / 15 * 100),
      financial_efficiency: Math.round((grossMargin > 0.8 ? 10 : grossMargin > 0.7 ? 8 : 6) / 10 * 100)
    },
    trend: 'improving', // Would be calculated based on historical data
    last_updated: analytics.generated_at
  };
}

