function calculateOverallDemand(factors) {
  const demandValues = {
    'surge': 1.0,
    'high': 0.8,
    'growing': 0.7,
    'increasing': 0.6,
    'medium': 0.5,
    'normal': 0.4,
    'stable': 0.3,
    'steady': 0.3
  };

  const totalDemand = Object.values(factors).reduce((sum, level) => {
    return sum + (demandValues[level] || 0.3);
  }, 0) / Object.keys(factors).length;

  return totalDemand > 0.7 ? 'very_high' : totalDemand > 0.5 ? 'high' : totalDemand > 0.3 ? 'medium' : 'low';
}

