      response_protocol: 'police_rights_guidance',
      escalation_level: 'high',
      immediate_lawyer: true
    },

    legal_document_urgency: {
      keywords: ['court', 'deadline', 'filing', 'urgent document', 'tomorrow'],
      response_protocol: 'document_emergency',
      escalation_level: 'medium',
      immediate_lawyer: false
    },

    contract_dispute: {
      keywords: ['contract breach', 'lawsuit', 'legal action', 'sue', 'sued'],
      response_protocol: 'dispute_guidance',
      escalation_level: 'high',
      immediate_lawyer: true
    },

    employment_issue: {
      keywords: ['fired', 'wrongful termination', 'harassment', 'discrimination'],
      response_protocol: 'employment_rights',
      escalation_level: 'medium',
      immediate_lawyer: true
    },

    property_issue: {
      keywords: ['eviction', 'landlord', 'property dispute', 'trespassing'],
      response_protocol: 'property_rights',
      escalation_level: 'medium',
      immediate_lawyer: false
    },

    business_emergency: {
      keywords: ['business closure', 'regulatory action', 'compliance violation'],
      response_protocol: 'business_continuity',
      escalation_level: 'high',
      immediate_lawyer: true
    }
  };

  for (const [type, config] of Object.entries(emergencyTypes)) {
    const matchCount = config.keywords.filter(keyword => inputText.includes(keyword)).length;

    if (matchCount > 0) {
      return {
        type: type,
        confidence: matchCount / config.keywords.length,
        protocol: config.response_protocol,
        escalation_level: config.escalation_level,
        immediate_lawyer_required: config.immediate_lawyer,
        matched_keywords: config.keywords.filter(keyword => inputText.includes(keyword))
      };
    }
  }

  return {
    type: 'general_emergency',
    confidence: 0.5,
    protocol: 'general_legal_emergency',
    escalation_level: 'medium',
    immediate_lawyer_required: false,
    matched_keywords: []
  };
}

