  if ((hour >= 9 && hour <= 11) || (hour >= 14 && hour <= 16)) score += 0.3;
  else if ((hour >= 11 && hour <= 14) || (hour >= 16 && hour <= 18)) score += 0.1;

  // Day scoring
  if (day >= 1 && day <= 3) score += 0.2; // Mon-Wed
  else if (day === 4 || day === 5) score += 0.1; // Thu-Fri

  // Date scoring (salary cycle)
  if ([1, 2, 3, 28, 29, 30, 31].includes(date)) score += 0.2;
  else if ([15, 16, 17].includes(date)) score += 0.1;

  return Math.min(score, 1.0);
}

