// üì± MOBILE APP BUILD ORCHESTRATOR
// Complete React Native build and deployment automation

const buildRequest = $input.first().json;

// üöÄ BUILD ORCHESTRATION ENGINE
const buildOrchestrator = {
  build_id: 'BUILD_' + Date.now(),
  timestamp: new Date().toISOString(),
  app_name: 'Lexi Voice AI',
  target_market: 'Nigeria',

  // Build configuration
  build_config: {
    platform: buildRequest.platform || 'both', // 'ios', 'android', 'both'
    build_type: buildRequest.build_type || 'release', // 'debug', 'release'
    environment: buildRequest.environment || 'production',
    version_increment: buildRequest.version_increment || 'patch',
    deploy_to_stores: buildRequest.deploy_to_stores || false
  },

  // Nigerian market configuration
  market_config: {
    primary_language: 'english',
    supported_languages: ['english', 'pidgin', 'yoruba', 'hausa', 'igbo'],
    currency: 'NGN',
    phone_format: '+234',
    timezone: 'Africa/Lagos',
    network_optimization: 'high' // For Nigerian connectivity
  },

  // Build pipeline
  build_pipeline: generateBuildPipeline(buildRequest),

  // Deployment configuration
  deployment_config: generateDeploymentConfig(buildRequest),

  // Quality assurance
  qa_config: generateQAConfig(buildRequest)
};

function generateBuildPipeline(request) {
  return {
    pre_build: {
      environment_setup: {
        node_version: '18.x',
        react_native_version: '0.72.0',
        java_version: '11',
        xcode_version: '15.0'
      },

      dependency_installation: {
        npm_packages: [
          '@react-native-async-storage/async-storage',
          '@react-native-voice/voice',
          'react-native-audio-recorder-player',
          '@supabase/supabase-js',
          'react-native-url-polyfill',
          'react-native-sound',
          '@react-navigation/native',
          '@react-navigation/stack',
          'react-native-vector-icons',
          'react-native-linear-gradient',
          'react-native-device-info',
          'react-native-permissions',
          'react-native-network-info'
        ],
        pod_install: true, // iOS dependencies
        gradle_sync: true  // Android dependencies
      },

      configuration_setup: {
        environment_variables: {
          MINIMAX_API_KEY: '${MINIMAX_API_KEY}',
          CLAUDE_API_KEY: '${CLAUDE_API_KEY}',
          SUPABASE_URL: '${SUPABASE_URL}',
          SUPABASE_ANON_KEY: '${SUPABASE_ANON_KEY}'
        },

        android_config: {
          signing_key: '${ANDROID_SIGNING_KEY}',
          store_password: '${ANDROID_STORE_PASSWORD}',
          key_alias: 'lexivoiceai',
          permissions: [
            'RECORD_AUDIO',
            'INTERNET',
            'ACCESS_NETWORK_STATE',
            'MODIFY_AUDIO_SETTINGS',
            'WAKE_LOCK'
          ]
        },

        ios_config: {
          provisioning_profile: '${IOS_PROVISIONING_PROFILE}',
          signing_certificate: '${IOS_SIGNING_CERTIFICATE}',
          bundle_id: 'com.lexivoiceai.app',
          permissions: [
            'NSMicrophoneUsageDescription',
            'NSSpeechRecognitionUsageDescription',
            'NSAppTransportSecurity'
          ]
        }
      }
    },

    build_execution: {
      android_build: {
        command: 'cd android && ./gradlew assembleRelease',
        output_path: 'android/app/build/outputs/apk/release/',
        file_name: 'app-release.apk',
        estimated_time: '10_minutes'
      },

      ios_build: {
        command: 'npx react-native run-ios --configuration Release',
        archive_command: 'xcodebuild -workspace ios/LexiVoiceAI.xcworkspace -scheme LexiVoiceAI archive',
        output_path: 'ios/build/',
        file_name: 'LexiVoiceAI.ipa',
        estimated_time: '15_minutes'
      },

      optimization: {
        bundle_size_optimization: true,
        image_compression: true,
        dead_code_elimination: true,
        minification: true
      }
    },

    post_build: {
      artifact_generation: {
        apk_signing: true,
        ipa_packaging: true,
        checksum_generation: true,
        build_metadata: true
      },

      testing: {
        unit_tests: 'npm run test',
        integration_tests: 'npm run test:integration',
        performance_tests: 'npm run test:performance',
        security_scan: 'npm run security:scan'
      }
    }
  };
}

function generateDeploymentConfig(request) {
  return {
    app_store_deployment: {
      ios_app_store: {
        enabled: request.build_config?.platform === 'ios' || request.build_config?.platform === 'both',
        app_store_connect_key: '${APP_STORE_CONNECT_KEY}',
        bundle_id: 'com.lexivoiceai.app',
        app_name: 'Lexi Voice AI',
        description: 'Nigeria\'s premier voice AI assistant supporting English, Pidgin, Yoruba, Hausa, and Igbo languages',
        keywords: 'voice assistant, Nigeria, AI, Pidgin, Yoruba, Hausa, Igbo, speech recognition',
        category: 'Productivity',
        age_rating: '4+',
        price: 'Free',
        in_app_purchases: true,
        phased_release: {
          enabled: true,
          phases: [
            { percentage: 1, duration: '24_hours' },
            { percentage: 5, duration: '48_hours' },
            { percentage: 20, duration: '72_hours' },
            { percentage: 50, duration: '96_hours' },
            { percentage: 100, duration: 'automatic' }
          ]
        }
      },

      google_play_store: {
        enabled: request.build_config?.platform === 'android' || request.build_config?.platform === 'both',
        service_account_key: '${GOOGLE_PLAY_SERVICE_ACCOUNT}',
        package_name: 'com.lexivoiceai.app',
        app_name: 'Lexi Voice AI',
        short_description: 'Nigerian voice AI assistant with multi-language support',
        full_description: 'Experience Nigeria\'s most advanced voice AI assistant. Lexi understands and speaks English, Nigerian Pidgin, Yoruba, Hausa, and Igbo. Perfect for busy Nigerians who need quick, intelligent assistance in their preferred language.',
        category: 'Productivity',
        content_rating: 'Everyone',
        price: 'Free',
        in_app_products: true,
        staged_rollout: {
          enabled: true,
          percentage: 5, // Start with 5% rollout
          halt_on_issues: true
        }
      }
    },

    internal_testing: {
      testflight_deployment: {
        enabled: true,
        tester_groups: ['internal_team', 'nigerian_beta_users'],
        automatic_distribution: true,
        feedback_collection: true
      },

      play_console_internal: {
        enabled: true,
        track: 'internal',
        tester_emails: [
          'team@lexivoiceai.com',
          'beta-testers@lexivoiceai.com'
        ],
        feedback_enabled: true
      }
    },

    cdn_deployment: {
      asset_distribution: {
        voice_models: 'https://cdn.lexivoiceai.com/models/',
        language_packs: 'https://cdn.lexivoiceai.com/languages/',
        app_updates: 'https://cdn.lexivoiceai.com/updates/',
        nigerian_optimized: true // CDN nodes in Lagos and Abuja
      }
    }
  };
}

function generateQAConfig(request) {
  return {
    automated_testing: {
      unit_tests: {
        framework: 'Jest',
        coverage_threshold: 80,
        test_suites: [
          'voice_recording_tests',
          'language_processing_tests',
          'offline_mode_tests',
          'nigerian_localization_tests'
        ]
      },

      integration_tests: {
        framework: 'Detox',
        test_scenarios: [
          'voice_to_text_flow',
          'language_switching',
          'offline_sync',
          'nigerian_phone_validation',
          'network_failure_handling'
        ]
      },

      performance_tests: {
        metrics: {
          app_startup_time: '< 2_seconds',
          voice_processing_latency: '< 1.5_seconds',
          memory_usage: '< 80_MB',
          battery_drain: '< 5%_per_hour',
          network_efficiency: 'optimized_for_3G'
        },

        load_testing: {
          concurrent_users: 1000,
          voice_requests_per_minute: 500,
          stress_test_duration: '30_minutes'
        }
      },

      security_testing: {
        vulnerability_scan: 'OWASP_Mobile_Top_10',
        api_security_test: true,
        data_encryption_test: true,
        certificate_pinning_test: true
      }
    },

    manual_testing: {
      device_compatibility: {
        android_devices: [
          'Samsung Galaxy A32 (Popular in Nigeria)',
          'Tecno Spark 7 (Nigerian market leader)',
          'Infinix Hot 10 (Budget segment)',
          'Xiaomi Redmi Note 10 (Mid-range)',
          'Google Pixel 6 (High-end testing)'
        ],

        ios_devices: [
          'iPhone SE 2020 (Budget iOS)',
          'iPhone 12 (Popular model)',
          'iPhone 13 (Current generation)',
          'iPad Air (Tablet testing)'
        ]
      },

      network_testing: {
        connection_types: [
          '2G (Edge case for rural Nigeria)',
          '3G (Common in Nigeria)',
          '4G LTE (Urban areas)',
          'WiFi (Office/home usage)',
          'Offline mode (No connectivity)'
        ],

        carriers: [
          'MTN Nigeria',
          'Airtel Nigeria', 
          'Glo Mobile',
          '9mobile'
        ]
      },

      language_testing: {
        voice_recognition_accuracy: {
          english: '> 95%',
          pidgin: '> 90%',
          yoruba: '> 85%',
          hausa: '> 85%',
          igbo: '> 85%'
        },

        cultural_appropriateness: {
          greetings: 'Nigerian customs',
          expressions: 'Local idioms',
          contexts: 'Cultural sensitivity'
        }
      }
    },

    user_acceptance_testing: {
      beta_testing: {
        locations: ['Lagos', 'Abuja', 'Port Harcourt', 'Kano', 'Ibadan'],
        user_groups: [
          'young_professionals',
          'students',
          'business_owners',
          'elderly_users',
          'rural_users'
        ],

        testing_scenarios: [
          'daily_voice_assistant_usage',
          'business_communication',
          'educational_queries',
          'emergency_situations',
          'entertainment_requests'
        ]
      }
    }
  };
}

// üìä BUILD MONITORING
buildOrchestrator.monitoring = {
  build_metrics: {
    build_duration: null,
    success_rate: null,
    error_count: 0,
    warning_count: 0,
    artifact_size: null
  },

  quality_gates: {
    code_coverage: { threshold: 80, status: 'pending' },
    security_scan: { threshold: 'no_high_severity', status: 'pending' },
    performance_test: { threshold: 'all_pass', status: 'pending' },
    build_size: { threshold: '< 50MB', status: 'pending' }
  },

  deployment_readiness: {
    ios_ready: false,
    android_ready: false,
    store_metadata_ready: false,
    certificates_valid: false
  }
};

// üöÄ NIGERIAN MARKET OPTIMIZATIONS
buildOrchestrator.nigerian_optimizations = {
  network_optimization: {
    data_compression: 'aggressive',
    image_optimization: 'webp_format',
    voice_compression: 'opus_codec',
    offline_capabilities: 'extensive',
    retry_logic: 'exponential_backoff'
  },

  localization: {
    currency_formatting: 'NGN_naira_symbol',
    date_formatting: 'dd/mm/yyyy',
    number_formatting: 'comma_separated',
    phone_formatting: '+234_format',
    address_formatting: 'nigerian_states'
  },

  cultural_adaptation: {
    greeting_patterns: 'time_based_greetings',
    response_style: 'respectful_and_friendly',
    humor_understanding: 'nigerian_context',
    cultural_references: 'local_knowledge',
    social_etiquette: 'nigerian_customs'
  },

  performance_optimization: {
    low_end_device_support: true,
    battery_optimization: 'aggressive',
    memory_management: 'efficient',
    startup_time: 'minimized',
    background_processing: 'limited'
  }
};

console.log(`üì± Mobile build orchestration initialized for ${buildOrchestrator.app_name} - Platform: ${buildOrchestrator.build_config.platform}`);

return [{ json: buildOrchestrator }];

// üçé iOS BUILD & DEPLOYMENT PIPELINE
// Complete iOS build automation for App Store deployment

const buildConfig = $input.first().json;

// üöÄ iOS BUILD EXECUTION
const iosBuild = {
  build_id: buildConfig.build_id + '_iOS',
  platform: 'iOS',
  timestamp: new Date().toISOString(),

  // Build environment setup
  environment_setup: {
    xcode_version: '15.0',
    ios_deployment_target: '12.0',
    swift_version: '5.0',
    cocoapods_version: '1.12.0'
  },

  // Build configuration
  build_configuration: {
    scheme: 'LexiVoiceAI',
    configuration: 'Release',
    workspace: 'ios/LexiVoiceAI.xcworkspace',
    export_method: 'app-store',
    bundle_id: 'com.lexivoiceai.app',
    team_id: '${APPLE_TEAM_ID}',
    provisioning_profile: '${IOS_PROVISIONING_PROFILE}'
  },

  // Build steps
  build_steps: {
    step_1: {
      name: 'Environment Preparation',
      commands: [
        'cd ios',
        'pod install --repo-update',
        'xcodebuild -version',
        'xcrun simctl list devices'
      ],
      estimated_time: '3_minutes'
    },

    step_2: {
      name: 'Project Build',
      commands: [
        'xcodebuild clean -workspace LexiVoiceAI.xcworkspace -scheme LexiVoiceAI',
        'xcodebuild build -workspace LexiVoiceAI.xcworkspace -scheme LexiVoiceAI -configuration Release'
      ],
      estimated_time: '8_minutes'
    },

    step_3: {
      name: 'Archive Creation',
      commands: [
        'xcodebuild archive -workspace LexiVoiceAI.xcworkspace -scheme LexiVoiceAI -configuration Release -archivePath build/LexiVoiceAI.xcarchive'
      ],
      estimated_time: '5_minutes'
    },

    step_4: {
      name: 'IPA Export',
      commands: [
        'xcodebuild -exportArchive -archivePath build/LexiVoiceAI.xcarchive -exportPath build -exportOptionsPlist ExportOptions.plist'
      ],
      estimated_time: '3_minutes'
    }
  },

  // App Store deployment
  app_store_deployment: {
    testflight_upload: {
      enabled: true,
      command: 'xcrun altool --upload-app --type ios --file build/LexiVoiceAI.ipa --username ${APPLE_ID} --password ${APP_SPECIFIC_PASSWORD}',
      beta_groups: ['Nigerian Beta Testers', 'Internal Team'],
      external_testing: true
    },

    app_store_submission: {
      enabled: buildConfig.build_config.deploy_to_stores,
      app_metadata: {
        name: 'Lexi Voice AI',
        subtitle: 'Nigerian Voice Assistant',
        description: `Nigeria's most advanced voice AI assistant that understands and speaks your language.\n\nFeatures:\n‚Ä¢ Voice recognition in English, Pidgin, Yoruba, Hausa, and Igbo\n‚Ä¢ Offline mode for areas with poor connectivity\n‚Ä¢ Nigerian cultural context understanding\n‚Ä¢ Fast response times optimized for Nigerian networks\n‚Ä¢ Privacy-focused with local data processing\n\nPerfect for:\n‚Ä¢ Busy professionals who need quick assistance\n‚Ä¢ Students learning in multiple Nigerian languages\n‚Ä¢ Business owners managing daily operations\n‚Ä¢ Anyone who prefers voice interaction over typing\n\nLexi understands Nigerian culture, slang, and context better than any other voice assistant. Experience the future of AI communication, designed specifically for Nigerians.`,
        keywords: 'voice assistant, Nigeria, AI, Pidgin, Yoruba, Hausa, Igbo, speech recognition, artificial intelligence',
        category: 'Productivity',
        age_rating: '4+',
        price_tier: 'Free'
      },

      app_review_information: {
        contact_email: 'support@lexivoiceai.com',
        contact_phone: '+2347025862292',
        review_notes: 'This app is designed specifically for the Nigerian market with support for Nigerian languages and cultural context. Please test with Nigerian English or Pidgin phrases for best results.'
      },

      release_settings: {
        release_type: 'manual', // Manual release after approval
        phased_release: true,
        pricing_territory: 'Nigeria',
        availability_date: 'upon_approval'
      }
    }
  },

  // Quality assurance
  quality_assurance: {
    automated_tests: {
      unit_tests: 'xcodebuild test -workspace LexiVoiceAI.xcworkspace -scheme LexiVoiceAI -destination "platform=iOS Simulator,name=iPhone 14"',
      ui_tests: 'xcodebuild test -workspace LexiVoiceAI.xcworkspace -scheme LexiVoiceAIUITests -destination "platform=iOS Simulator,name=iPhone 14"',
      performance_tests: true
    },

    static_analysis: {
      xcode_analyzer: true,
      swiftlint: true,
      security_scan: true
    },

    device_testing: {
      simulator_testing: ['iPhone SE', 'iPhone 12', 'iPhone 14', 'iPad Air'],
      physical_device_testing: true // For voice and network testing
    }
  },

  // Nigerian market specific testing
  nigerian_market_testing: {
    language_testing: {
      english: 'Nigerian English accent and phrases',
      pidgin: 'Common Nigerian Pidgin expressions',
      yoruba: 'Basic Yoruba greetings and responses',
      hausa: 'Northern Nigerian Hausa usage',
      igbo: 'Eastern Nigerian Igbo phrases'
    },

    network_testing: {
      connection_simulation: ['3G', '4G', 'WiFi', 'Poor_connectivity'],
      carrier_testing: ['MTN', 'Airtel', 'Glo', '9mobile'],
      offline_mode: 'Extensive offline functionality testing'
    },

    cultural_testing: {
      greetings: 'Time-appropriate Nigerian greetings',
      responses: 'Culturally appropriate responses',
      context_understanding: 'Nigerian cultural references and idioms'
    }
  }
};

// üìä BUILD MONITORING
iosBuild.monitoring = {
  build_status: 'in_progress',
  current_step: 'environment_preparation',
  estimated_completion: calculateEstimatedCompletion(),

  success_criteria: {
    build_successful: false,
    tests_passed: false,
    archive_created: false,
    ipa_exported: false,
    testflight_uploaded: false
  },

  performance_metrics: {
    build_duration: null,
    artifact_size: null,
    test_coverage: null,
    memory_usage: null
  }
};

function calculateEstimatedCompletion() {
  const totalMinutes = 3 + 8 + 5 + 3; // Sum of all step durations
  const completionTime = new Date();
  completionTime.setMinutes(completionTime.getMinutes() + totalMinutes);
  return completionTime.toISOString();
}

// üîÑ POST-BUILD ACTIONS
iosBuild.post_build_actions = {
  artifact_storage: {
    ipa_storage: 's3://lexivoiceai-builds/ios/',
    dsym_storage: 's3://lexivoiceai-builds/ios/dsyms/',
    build_logs: 's3://lexivoiceai-builds/ios/logs/',
    retention_period: '90_days'
  },

  notifications: {
    slack_notification: {
      channel: '#mobile-builds',
      message: `iOS build ${iosBuild.build_id} completed successfully! üéâ\nVersion: ${buildConfig.version}\nTestFlight: Available for testing`,
