// Voice Session Analytics
const voiceSessions = $('Collect Voice Session Data').all();
const emergencies = $('Collect Emergency Detection Data').all();
const documents = $('Collect Document Generation Data').all();
const payments = $('Collect Payment Data').all();
const consultations = $('Collect Consultation Data').all();

// Calculate Voice Session Metrics
const voiceMetrics = {
  totalSessions: voiceSessions.length,
  averageConfidence: voiceSessions.reduce((sum, session) => sum + (session.json.confidence || 0), 0) / voiceSessions.length || 0,
  languageDistribution: {},
  intentDistribution: {},
  averageSessionDuration: 0,
  successfulSessions: voiceSessions.filter(s => s.json.status === 'completed').length
};

// Language distribution
voiceSessions.forEach(session => {
  const lang = session.json.language || 'unknown';
  voiceMetrics.languageDistribution[lang] = (voiceMetrics.languageDistribution[lang] || 0) + 1;
});

// Intent distribution
voiceSessions.forEach(session => {
  const intent = session.json.intent || 'unknown';
  voiceMetrics.intentDistribution[intent] = (voiceMetrics.intentDistribution[intent] || 0) + 1;
});

// Emergency Detection Metrics
const emergencyMetrics = {
  totalEmergencies: emergencies.length,
  trueFalseRatio: {
    medical: emergencies.filter(e => e.json.emergency_type === 'medical').length,
    security: emergencies.filter(e => e.json.emergency_type === 'security').length,
    legal: emergencies.filter(e => e.json.emergency_type === 'legal').length,
    falsePositives: emergencies.filter(e => e.json.status === 'false_positive').length
  },
  averageConfidence: emergencies.reduce((sum, em) => sum + (em.json.confidence_score || 0), 0) / emergencies.length || 0,
  responseTime: emergencies.reduce((sum, em) => {
    const created = new Date(em.json.created_at);
    const responded = new Date(em.json.last_follow_up || em.json.created_at);
    return sum + (responded - created);
  }, 0) / emergencies.length || 0
};

// Document Generation Metrics
const documentMetrics = {
  totalDocuments: documents.length,
  documentTypes: {},
  languageDistribution: {},
  stateDistribution: {},
  averageGenerationTime: 0,
  successRate: documents.filter(d => d.json.status === 'completed').length / documents.length || 0,
  legalComplianceRate: documents.filter(d => d.json.metadata?.legalCompliance === true).length / documents.length || 0
};

// Document type distribution
documents.forEach(doc => {
  const type = doc.json.document_type || 'unknown';
  documentMetrics.documentTypes[type] = (documentMetrics.documentTypes[type] || 0) + 1;

  const lang = doc.json.language || 'unknown';
  documentMetrics.languageDistribution[lang] = (documentMetrics.languageDistribution[lang] || 0) + 1;

  const state = doc.json.state || 'unknown';
  documentMetrics.stateDistribution[state] = (documentMetrics.stateDistribution[state] || 0) + 1;
});

// Payment & Revenue Metrics
const paymentMetrics = {
  totalRevenue: payments.reduce((sum, payment) => sum + (parseFloat(payment.json.amount) || 0), 0),
  totalTransactions: payments.length,
  successfulPayments: payments.filter(p => p.json.status === 'successful').length,
  failedPayments: payments.filter(p => p.json.status === 'failed').length,
  subscriptionDistribution: {},
  paymentMethods: {},
  averageTransactionValue: 0
};

if (payments.length > 0) {
  paymentMetrics.averageTransactionValue = paymentMetrics.totalRevenue / payments.length;

  payments.forEach(payment => {
    const plan = payment.json.subscription_plan || 'unknown';
    paymentMetrics.subscriptionDistribution[plan] = (paymentMetrics.subscriptionDistribution[plan] || 0) + 1;

    const method = payment.json.payment_method || 'unknown';
    paymentMetrics.paymentMethods[method] = (paymentMetrics.paymentMethods[method] || 0) + 1;
  });
}

// Consultation Metrics
const consultationMetrics = {
  totalConsultations: consultations.length,
  scheduledConsultations: consultations.filter(c => c.json.status === 'scheduled').length,
  completedConsultations: consultations.filter(c => c.json.status === 'completed').length,
  cancelledConsultations: consultations.filter(c => c.json.status === 'cancelled').length,
  legalAreaDistribution: {},
  averageConsultationFee: consultations.reduce((sum, c) => sum + (parseFloat(c.json.consultation_fee) || 0), 0) / consultations.length || 0,
  urgencyDistribution: {}
};

consultations.forEach(consultation => {
  const area = consultation.json.legal_area || 'unknown';
  consultationMetrics.legalAreaDistribution[area] = (consultationMetrics.legalAreaDistribution[area] || 0) + 1;

  const urgency = consultation.json.urgency || 'unknown';
  consultationMetrics.urgencyDistribution[urgency] = (consultationMetrics.urgencyDistribution[urgency] || 0) + 1;
});

// System Performance Metrics
const systemMetrics = {
  apiResponseTimes: {
    voice_processing: voiceSessions.reduce((sum, s) => sum + (s.json.processing_time_ms || 0), 0) / voiceSessions.length || 0,
    document_generation: documents.reduce((sum, d) => sum + (d.json.metadata?.processing_time_ms || 0), 0) / documents.length || 0,
    emergency_response: emergencies.reduce((sum, e) => sum + (e.json.response_time_ms || 0), 0) / emergencies.length || 0
  },
  errorRates: {
    voice_sessions: voiceSessions.filter(s => s.json.status === 'error').length / voiceSessions.length || 0,
    document_generation: documents.filter(d => d.json.status === 'error').length / documents.length || 0,
    payment_processing: payments.filter(p => p.json.status === 'failed').length / payments.length || 0
  },
  userEngagement: {
    activeUsers: new Set(voiceSessions.map(s => s.json.user_id)).size,
    returningUsers: voiceSessions.filter(s => {
      const userSessions = voiceSessions.filter(session => session.json.user_id === s.json.user_id);
      return userSessions.length > 1;
    }).length
  }
};

// Quality Scores
const qualityMetrics = {
  overallSystemHealth: {
    voice_processing: Math.max(0, 100 - (systemMetrics.errorRates.voice_sessions * 100)),
    document_generation: Math.max(0, 100 - (systemMetrics.errorRates.document_generation * 100)),
    emergency_detection: Math.max(0, 100 - (emergencyMetrics.trueFalseRatio.falsePositives / emergencyMetrics.totalEmergencies * 100 || 0)),
    payment_processing: Math.max(0, 100 - (systemMetrics.errorRates.payment_processing * 100))
  },
  userSatisfaction: {
    voice_confidence: voiceMetrics.averageConfidence * 100,
    document_compliance: documentMetrics.legalComplianceRate * 100,
    emergency_accuracy: emergencyMetrics.averageConfidence * 100
  },
  nigerianOptimization: {
    localLanguageUsage: {
      english: voiceMetrics.languageDistribution.english || 0,
      yoruba: voiceMetrics.languageDistribution.yoruba || 0,
      hausa: voiceMetrics.languageDistribution.hausa || 0,
      igbo: voiceMetrics.languageDistribution.igbo || 0,
      pidgin: voiceMetrics.languageDistribution.pidgin || 0
    },
    stateDistribution: documentMetrics.stateDistribution,
    localPaymentMethods: paymentMetrics.paymentMethods
  }
};

// Business Intelligence
const businessMetrics = {
  revenue: {
    totalRevenue: paymentMetrics.totalRevenue,
    monthlyRecurringRevenue: payments.filter(p => p.json.subscription_plan && p.json.subscription_plan !== 'one_time').reduce((sum, p) => sum + parseFloat(p.json.amount), 0),
    averageRevenuePerUser: paymentMetrics.totalRevenue / systemMetrics.userEngagement.activeUsers || 0,
    subscriptionTierRevenue: {}
  },
  growth: {
    newUsers: systemMetrics.userEngagement.activeUsers - systemMetrics.userEngagement.returningUsers,
    userRetention: systemMetrics.userEngagement.returningUsers / systemMetrics.userEngagement.activeUsers || 0,
    documentGenerationGrowth: documentMetrics.totalDocuments,
    consultationGrowth: consultationMetrics.totalConsultations
  },
  operationalEfficiency: {
    documentGenerationRate: documentMetrics.successRate,
    emergencyResponseTime: emergencyMetrics.responseTime / 60000, // in minutes
    lawyerUtilization: consultationMetrics.completedConsultations / consultationMetrics.scheduledConsultations || 0
  }
};

// Calculate subscription tier revenue
for (const [plan, count] of Object.entries(paymentMetrics.subscriptionDistribution)) {
  const planPayments = payments.filter(p => p.json.subscription_plan === plan);
  businessMetrics.revenue.subscriptionTierRevenue[plan] = planPayments.reduce((sum, p) => sum + parseFloat(p.json.amount), 0);
}

// Alerts and Recommendations
const alerts = [];
const recommendations = [];

// Check for critical issues
if (systemMetrics.errorRates.voice_sessions > 0.1) {
  alerts.push({ type: 'critical', message: 'High voice processing error rate detected', value: systemMetrics.errorRates.voice_sessions });
}

if (emergencyMetrics.trueFalseRatio.falsePositives / emergencyMetrics.totalEmergencies > 0.3) {
  alerts.push({ type: 'warning', message: 'High false positive rate in emergency detection', value: emergencyMetrics.trueFalseRatio.falsePositives });
}

if (systemMetrics.errorRates.payment_processing > 0.05) {
  alerts.push({ type: 'critical', message: 'Payment processing issues detected', value: systemMetrics.errorRates.payment_processing });
}

// Generate recommendations
if (voiceMetrics.languageDistribution.english > voiceMetrics.totalSessions * 0.8) {
  recommendations.push({ type: 'growth', message: 'Consider promoting local language features', priority: 'medium' });
}

if (documentMetrics.legalComplianceRate < 0.9) {
  recommendations.push({ type: 'quality', message: 'Improve legal compliance validation', priority: 'high' });
}

if (businessMetrics.growth.userRetention < 0.6) {
  recommendations.push({ type: 'retention', message: 'Implement user retention strategies', priority: 'high' });
}

return [{
  json: {
    reportId: $('Initialize Analytics Collection').item.json.reportId,
    timestamp: $('Initialize Analytics Collection').item.json.timestamp,
    timeRange: $('Initialize Analytics Collection').item.json.timeRange,
    voiceMetrics,
    emergencyMetrics,
    documentMetrics,
    paymentMetrics,
    consultationMetrics,
    systemMetrics,
    qualityMetrics,
    businessMetrics,
    alerts,
    recommendations,
    summary: {
      totalUsers: systemMetrics.userEngagement.activeUsers,
      totalRevenue: paymentMetrics.totalRevenue,
      systemHealth: (qualityMetrics.overallSystemHealth.voice_processing + qualityMetrics.overallSystemHealth.document_generation + qualityMetrics.overallSystemHealth.emergency_detection + qualityMetrics.overallSystemHealth.payment_processing) / 4,
      criticalAlerts: alerts.filter(a => a.type === 'critical').length,
      highPriorityRecommendations: recommendations.filter(r => r.priority === 'high').length
    }
  }
}];

